mapper.select:
 	SELECT * FROM
	(SELECT rownum AS rnum, A.* FROM
		( SELECT * FROM hk_board 
			WHERE btype=#{btype} AND



					title LIKE #{keyword}


					writer LIKE #{keyword}




					(title LIKE #{keyword}
					OR writer LIKE #{keyword})



		ORDER BY tnum DESC
		)
	A)
WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:
UPDATE hk_board
	SET title=#{title},
		content=#{content}
WHERE bno=#{bno}

UPDATE hk_board
	SET viewcnt = viewcnt + 1
WHERE bno = #{bno} AND btype=#{btype}

mapper.select:
SELECT COUNT(*)
FROM hk_board WHERE btype=#{btype} AND



			title LIKE #{keyword}


			writer LIKE #{keyword}



		(title LIKE #{keyword}
		OR writer LIKE #{keyword})
