mapper.update:
UPDATE hk_member
   SET pw = #{pw}
 WHERE id = #{id}

UPDATE hk_member
   SET email = #{email},
  	   phone = #{phone}
 WHERE id = #{id}

UPDATE hk_member
   SET name = #{name},
   	   email = #{email},
  	   phone = #{phone},
  	   pw = #{pw}
 WHERE id = #{id}

mapper.select:
SELECT * FROM hk_member
WHERE type = #{type}
ORDER BY mno

SELECT COUNT(*) 
FROM hk_member
WHERE id = #{id} OR phone = #{phone} OR email = #{email}
