mapper.select:
SELECT * FROM
	(SELECT rownum AS rnum, A.* FROM 
		( SELECT * FROM hk_lecture_board WHERE



					btype='0' AND


					btype='1' AND


					btype='2' AND






					title LIKE #{keyword}


					content LIKE #{keyword}


					writer LIKE #{keyword}



				(title LIKE #{keyword}
				OR writer LIKE #{keyword}
				OR content LIKE #{keyword})


			ORDER BY bno DESC
		)
	A)
WHERE rnum BETWEEN #{start} AND #{end}

SELECT COUNT(*)
FROM hk_lecture_board WHERE



			btype='0' AND


			btype='1' AND


			btype='2' AND






			title LIKE #{keyword}


			content LIKE #{keyword}


			writer LIKE #{keyword}



		(title LIKE #{keyword}
		OR writer LIKE #{keyword}
		OR content LIKE #{keyword})

mapper.update:
UPDATE hk_lecture_board
	SET viewcnt = viewcnt + 1
WHERE bno = #{bno}

UPDATE hk_lecture_board
	SET title=#{title},
		content=#{content}
WHERE bno=#{bno}
