mapper.select:
SELECT COUNT(*)
FROM hk_lecture WHERE



			lname LIKE #{keyword}


			tname LIKE #{keyword}



		lname LIKE #{keyword}
		OR tname LIKE #{keyword}

SELECT * FROM
	(SELECT rownum AS rnum, A.* FROM
		( SELECT *
		FROM hk_lecture WHERE



					lname LIKE #{keyword}


					tname LIKE #{keyword}



				lname LIKE #{keyword}
				OR tname LIKE #{keyword}



		ORDER BY lno DESC


		ORDER BY rgstrmem DESC, lno DESC

		)
	A)
WHERE rnum BETWEEN #{start} AND #{end}

SELECT COUNT(*)
FROM hk_wish
WHERE lno = #{lno} AND id = #{id}

SELECT COUNT(*)
FROM hk_wish
WHERE id = #{id}

     	SELECT a.*
		FROM hk_lecture a, hk_wish b
		WHERE a.lno = b.lno AND b.id=#{id}
